import { useState } from 'react';
import axios from 'axios';
import "../styles/Login.css";
import "../styles/Text.css";
import gif_login from '../assets/login-design.gif';
import logo from '../assets/logo-design.png';

function Login() {
    const [inputValue, setInputValue] = useState(''); // For username
    const [password, setPassword] = useState(''); // For password

    const onPressLearnMore = () => {
        console.log('Button clicked', { inputValue, password }); // Log input values
    };

    const handleChange = (event) => {
        const { name, value } = event.target; // Destructure name and value
        if (name === "username") {
            setInputValue(value); // Update username
        } else if (name === "password") {
            setPassword(value); // Update password
        }
    };

    return (
        <div className="background-container">
            <img src={gif_login} className="gif_login" alt="yo mama" />
            <div className="login-container">
                <p className="header" style = {{marginTop: 100}}>Binary Baes:</p>
                <img src={logo} className="logo" alt="logo design" />
                <p className="small-text" style={{marginRight: 200}}>Username:</p>
                <input 
                    name="username"
                    type="text"
                    value={inputValue}
                    onChange={handleChange}
                />
                <p className="small-text" style={{marginRight: 200}} >Password:</p>
                <input 
                    name="password"
                    type="password" // Change to "password" for security
                    value={password}
                    onChange={handleChange}
                />
                <button className = "button-design" onClick={onPressLearnMore} style={{marginTop: 80}}>
                    Submit
                </button> {/* Standard HTML button */}
            </div>
        </div>
    );
}



