import { useState } from 'react';
import axios from 'axios';
import "../styles/Login.css";
import "../styles/Text.css";
import gif_login from '../assets/login-design.gif';
import logo from '../assets/logo-design.png';


function Login() {
    const [inputValue, setInputValue] = useState('');

    const handleChange = (event) => {
        setInputValue(event.target.value); // Update state on input change
    };


    return (
        <div className="background-container">
            {<img src={gif_login} className = "gif_login" alt= "yo mama" />}
            <div className = "login-container">
                <p className = "small-text">Password: </p>
                {<img src={logo} className = "logo" alt= "logo design" />}
                <p className="small-text">Username:</p> {/* Corrected subheader */}
                <input 
                type="text" // Specify the input type
                value={inputValue} // Controlled input
                onChange={handleChange} // Handle input changes
                placeholder="" // Placeholder text
                />
                <p className = "small-text">Password: </p>
                <input 
                type="text" // Specify the input type
                value={inputValue} // Controlled input
                onChange={handleChange} // Handle input changes
                placeholder="" // Placeholder text
                />
            </div>
        </div>
    );
}



